mapper.select:
SELECT DISTINCT a.id,a.`name`,a.`begin_date`,a.`end_date`,a.`theme` FROM t_scheduled s LEFT JOIN t_post_assignment pa ON s.`post_assignment_id` = pa.`id` LEFT JOIN t_activity_assignment aa ON aa.id = pa.`activity_assignment_id` LEFT JOIN t_activity a ON a.id = aa.`activity_id` LEFT JOIN t_post p ON p.id = pa.`post_id` WHERE s.`user_id` = #{userId}
AND p.name = #{postName}
ORDER BY a.id DESC

SELECT DISTINCT a.id,a.name,a.theme,a.category_id,c.name AS category,a.status,DATE(a.begin_date) AS beginDate, DATE(a.end_date) AS endDate FROM t_activity a LEFT JOIN t_activity_assignment aa ON a.`id` = aa.`activity_id` LEFT JOIN t_category c ON a.`category_id` = c.`id` WHERE a.`del_flag` = 0
AND a.`status` = #{status}
AND aa.`location_id` = #{locationId}
AND a.`category_id` = #{categoryId}
AND a.`name` LIKE concat('%',#{activityName},'%')
AND a.`allowed_department_id` in (#{departmentId},0,-1)
AND a.`allowed_department_id` = -1
ORDER BY DATE(a.`begin_date`) DESC
